import React, { useState } from "react";
import AddIcon from '@material-ui/icons/Add';
import CloseIcon from '@material-ui/icons/Close';
import Fab from '@material-ui/core/Fab';
import { Zoom } from "@material-ui/core";

function CreateArea(props) {
  const [isExpanded,setisExpanded] = useState(props.inEditMode);

  const [noteText, setnoteText] = useState({ title: "", content: "" });

  const [inEditMode,setEditMode] = useState()
  function handleTitle(event) {
    const { name, value } = event.target;
    setnoteText((prevnoteText) => {
      return { ...prevnoteText, [name]: value };
    });
  } 

  function expand(){
    // console.log(isExpanded);
    setisExpanded(!isExpanded);
  }

  return (
    <div>
      {(isExpanded || props.inEditMode) && (
        <form className="create-area">
          <input
            name="title"
            onChange={handleTitle}
            value={noteText.title}
            placeholder="Title"
          />
          <textarea
            onChange={handleTitle}
            name="content"
            placeholder="Note"
            value={noteText.content}
            rows="3"
          />
          <Zoom in={isExpanded || props.inEditMode}>
            <Fab
              onClick={(event) => {
                props.onAdd(noteText);
                setnoteText({ title: "", content: "" });
                event.preventDefault();
                setisExpanded(!isExpanded);
                props.setEditMode(false);
                console.log(props.inEditMode);
              }}
            >
              <AddIcon />
            </Fab>
          </Zoom>

        </form>
      )}
      <button onClick={expand} className="createButton">{!isExpanded?<AddIcon/>:<CloseIcon/>}</button>
    </div>
  );
}

export default CreateArea;
